function checkList(){

	var trueList="",falseList="";
	var check_obj = $("input[name='C_state']");
	var trueStep=0,falseStep=0;
	for ( var i = 0; i < check_obj.length; i++) {
		if (check_obj.get(i).checked == true) {
			trueStep++;
			if (trueStep==1){
				trueList += check_obj.get(i).value;
			}else{
				trueList += "_"+check_obj.get(i).value ;
			}
		
		}else{
			falseStep++;
			if (falseStep==1){
				falseList += check_obj.get(i).value;
			}else{
				falseList += "_"+check_obj.get(i).value ;
			}
		}
	}
	
	$("#threadRows").attr("action", "article_batchPassed.action?trueListStr="+trueList+"&falseListStr="+falseList+"&gid={$$gid$$}");

	//alert(trueList);
	//alert(falseList);
return true;
}
