			}
			
		}
	 });
}

function poll(tid,sp){
	
	$.ajax({
		url:'{$$contextPath$$}/ajax_art_poll_v.action?tid='+tid+'&sp='+sp,
		type: 'post',
		success:function(data){
		
			var re=data;
			if (re=='success'){
				
				pollWrite(tid);
			}else if (re=='exists_true'){
				alert('你忘了你称赞过他了？');
			}else if (re=='exists_false'){
				alert('你上次把他扔得满脸蛋汤，别再折磨他了！');
			}else if (re=='exists_neu'){
				alert('酱油店没关门呢！');
			}else if (re=='noLogin'){
				alert('您需要登录才能进行操作！');
			}else if (re=='timeOut'){
				alert('过时了，下次再来吧！');
			}else{
				alert('系统或参数错误！');
			}
			
		}
	 });
 
}

